function createHappyBar(a){a.happyBarProgress=a.add.image(16,16,"happy_bar_progress"),a.happyBarProgress.scale.setTo(a.hardScale),a.happyBarProgress.fixedToCamera=!0,a.happyBarTenth=a.happyBarProgress.width/10,a.happyBarHundredth=a.happyBarTenth/10,a.happyBarOutline=a.add.image(16,16,"happy_bar_outline"),a.happyBarOutline.scale.setTo(a.hardScale),a.happyBarOutline.fixedToCamera=!0,a.time.events.loop(Phaser.Timer.SECOND,function(){a.happyBarProgress.width>0&&(a.happyBarProgress.width=Math.floor(a.happyBarProgress.width-a.happyBarHundredth)),a.happyBarProgress.width<0&&(a.happyBarProgress.width=0)},a)}function createDeathLabel(a){a.deathLabel=a.add.text(345,16,"Death Count: "+a.deathCount,{font:"24px Arial",fill:"#fff"}),a.deathLabel.fixedToCamera=!0,a.deathLabel.inputEnabled=!0,a.deathLabel.events.onInputUp.add(function(){console.log("hit")})}function updateDeathLabel(a){a.deathCount++,a.deathLabel.setText("Death Count: "+a.deathCount)}